    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{# bỏ navigation bar ở trang login #}
{% block nav %} {% endblock %}  

{% block body %}
<div class=\"container col-md-5 text-center\">
    <div class=\"container-fluid\">
    {% for message in app.session.flashbag.get('Warning') %}
        <p class=\"alert alert-danger text-uppercase text-center\">{{ message }}</p>
    {% endfor %}

    <form method=\"post\">
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}

        {% if app.user %}
            <div class=\"mb-3\">
                You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
            </div>
        {% endif %}

        <h1 class=\"h3 mb-3 font-weight-normal\">User Login</h1>
        <label for=\"inputUsername\">Username</label>
        <input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" class=\"form-control\" autocomplete=\"username\" required autofocus>
        <br>
        <label for=\"inputPassword\">Password</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>

        <input type=\"hidden\" name=\"_csrf_token\"
            value=\"{{ csrf_token('authenticate') }}\"
        >
        <br>
        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>  

        <button class=\"btn btn-lg btn-primary\" type=\"submit\">
            Login
        </button>
    </form>
    <h5>
        <a class=\"nav-link mt-4\" href=\"{{ path('app_register') }}\">Register new account</a>
    </h5>
</div>
{% endblock %}", "security/login.html.twig", "C:\\xampp\\htdocs\\projectweb\\webg301-gch0904-2-\\student_manage\\templates\\security\\login.html.twig");
    }
